package week1.day1;

public class ConvertNegToPos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=-40;
		System.out.println(n + "is a negative number");
		
		if(n<0)
		{
			n = n *-1;
			System.out.println(n +" is now converted to positive number");
		}

	}

}
